/**
 * Get raw text between two tokens.
 */
function textBetween ( sourceCode, left, right )
{
    return sourceCode.text.slice ( left.range [ 1 ], right.range [ 0 ] );
}

/**
 * Returns true if the gap between tokens is *only* spaces, and length >= 1.
 * Newlines/tabs are NOT treated as valid spaces here.
 */
function hasOnlySpacesBetween ( sourceCode, left, right )
{
    const _text = textBetween ( sourceCode, left, right );

    return _text.length > 0 && /^[ ]+$/.test ( _text );
}

/**
 * Returns true if there is *no* whitespace at all between two tokens.
 */
function isTight ( sourceCode, left, right )
{
    return left && right && left.range [ 1 ] === right.range [ 0 ];
}

/**
 * Returns true if there is *some* whitespace between two tokens (spaces, tabs or newlines).
 */
function hasWhitespaceBetween ( sourceCode, left, right )
{
    const _text = textBetween ( sourceCode, left, right );

    return /\s/.test ( _text );
}

module.exports =
{
    textBetween,
    hasOnlySpacesBetween,
    isTight,
    hasWhitespaceBetween,
};
